    estado = request.args.get("estados");  # Pega o estado que o usuario pesquisar.
    newlist = [x for x in lista if x == estado]
    if estado != None:
        newlist = lista[estado]
    else:
        newlist = lista['ES']

    return render_template("info.html", info_lista=newlist)

